export const getPresetCategories = () => PRESETS.map(p => p.name).sort();

export const getUndercoverPresetCategories = () => UNDERCOVER_PRESETS.map(p => p.name).sort();

export const getRandomPreset = (difficulty: Difficulty, category?: string): { category: string, word: string } => {
  let availableCategories = PRESETS;
  
  if (category && category !== "Random") {
    availableCategories = PRESETS.filter(p => p.name === category);
  }

  if (availableCategories.length === 0) {
    availableCategories = PRESETS;
  }

  const randomCategory = availableCategories[Math.floor(Math.random() * availableCategories.length)];
  const words = randomCategory.words[difficulty] || randomCategory.words['MEDIUM'];
  const randomWord = words[Math.floor(Math.random() * words.length)];

  return {
    category: randomCategory.name,
    word: randomWord
  };
};

export const getRandomUndercoverPreset = (difficulty: Difficulty, category?: string): { category: string, secretWord: string, imposterWord: string } => {
  let availableCategories = UNDERCOVER_PRESETS;
  
  if (category && category !== "Random") {
    availableCategories = UNDERCOVER_PRESETS.filter(p => p.name === category);
  }

  if (availableCategories.length === 0) {
    availableCategories = UNDERCOVER_PRESETS;
  }

  const randomCategory = availableCategories[Math.floor(Math.random() * availableCategories.length)];
  const pairs = randomCategory.pairs[difficulty] || randomCategory.pairs['MEDIUM'];
  const randomPair = pairs[Math.floor(Math.random() * pairs.length)];

  // Randomly assign which word is secret vs imposter to add variety even with same pair
  const swap = Math.random() > 0.5;

  return {
    category: randomCategory.name,
    secretWord: swap ? randomPair[1] : randomPair[0],
    imposterWord: swap ? randomPair[0] : randomPair[1]
  };
};